import { compile } from 'path-to-regexp';
export const mergeExtension = users => {
  const output = [];
  return {
    middleware: output.concat(users)
  };
};
export const toMessage = (dig, e) => {
  const message = e instanceof Error ? e.message : e;

  if (message) {
    return `${dig}: ${message}`;
  } else {
    return dig;
  }
};
export const noop = () => {// noop
};
export const createErrorDocument = (status, text) => {
  const title = `${status}: ${text}`;
  return `<!DOCTYPE html>
  <html lang="en">
  <head>
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width">
    <title>${title}</title>
    <style>
      html,body {
        margin: 0;
      }

      .page-container {
        color: #000;
        background: #fff;
        height: 100vh;
        text-align: center;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
      }
    </style>
  </head>
  <body>
    <div class="page-container">
    <h1>${status}</h1>
    <div>${text}</div>
  </body>
  </html>
  `;
};
export const createMiddlewareCollecter = () => {
  const webMiddlewares = [];
  const apiMiddlewares = [];

  const addWebMiddleware = input => {
    webMiddlewares.push(input);
  };

  const addAPIMiddleware = input => {
    apiMiddlewares.push(input);
  };

  const getMiddlewares = () => ({
    web: webMiddlewares,
    api: apiMiddlewares
  });

  return {
    getMiddlewares,
    addWebMiddleware,
    addAPIMiddleware
  };
};
export const toPath = (reg, params) => {
  const fn = compile(reg, {
    encode: encodeURIComponent
  });
  return fn(params);
};
export const getStaticReg = (output = {}) => {
  const {
    favicon,
    faviconByEntries,
    cssPath,
    jsPath,
    mediaPath
  } = output;
  const favicons = prepareFavicons(favicon, faviconByEntries);
  const staticFiles = [cssPath, jsPath, mediaPath].filter(v => Boolean(v));
  const staticPathRegExp = new RegExp(`^/(static/|upload/|favicon.ico|icon.png${favicons.length > 0 ? `|${favicons.join('|')}` : ''}|${staticFiles.join('|')})`);
  return staticPathRegExp;
};
export const prepareFavicons = (favicon, faviconByEntries) => {
  const faviconNames = [];

  if (favicon) {
    faviconNames.push(favicon.substring(favicon.lastIndexOf('/') + 1));
  }

  if (faviconByEntries) {
    Object.keys(faviconByEntries).forEach(f => {
      const curFavicon = faviconByEntries[f];

      if (curFavicon) {
        faviconNames.push(curFavicon.substring(curFavicon.lastIndexOf('/') + 1));
      }
    });
  }

  return faviconNames;
};